import React, { createContext, useContext, useMemo, useState } from 'react';
import { useColorScheme } from 'react-native';
import { DarkTheme as NavDarkTheme, DefaultTheme as NavLightTheme } from '@react-navigation/native';

type ThemeTokens = {
  dark: boolean;
  background: string;
  card: string;
  text: string;
  muted: string;
  primary: string;
  accent: string;
  border: string;
  navTheme: any;
};

const light: ThemeTokens = {
  dark: false,
  background: '#F8FAFC',
  card: '#FFFFFF',
  text: '#0F172A',
  muted: '#64748B',
  primary: '#007AFF',
  accent: '#8B5CF6',
  border: '#E2E8F0',
  navTheme: NavLightTheme
};

const dark: ThemeTokens = {
  dark: true,
  background: '#0B1220',
  card: '#071226',
  text: '#E6EEF8',
  muted: '#94A3B8',
  primary: '#0EA5E9',
  accent: '#7C3AED',
  border: '#142032',
  navTheme: NavDarkTheme
};

const ThemeContext = createContext<{
  theme: ThemeTokens;
  toggle: () => void;
}>({ theme: light, toggle: () => {} });

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const sys = useColorScheme();
  const [isDark, setIsDark] = useState<boolean>(sys === 'dark');

  const toggle = () => setIsDark(v => !v);

  const theme = useMemo(() => (isDark ? dark : light), [isDark]);

  const value = useMemo(() => ({ theme, toggle }), [theme]);

  return <ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>;
}

export function useTheme() {
  return useContext(ThemeContext);
}

export default ThemeProvider;
