import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Pressable, TextInput } from 'react-native';
import { auth, db } from '../firebase/initFirebase';
import { signOut } from 'firebase/auth';
import { doc, getDoc, setDoc, serverTimestamp } from 'firebase/firestore';
import Toast from 'react-native-toast-message';

interface ProfileData {
  name?: string;
  institution?: string;
  position?: string;
  contact?: string;
}

function initials(name?: string) {
  if (!name) return '';
  return name.split(' ').map(s => s[0]).slice(0,2).join('').toUpperCase();
}

export default function ProfileScreen() {
  const user = auth.currentUser;
  const [profile, setProfile] = useState<ProfileData>({});
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    if (!user) return;
    const ref = doc(db, 'users', user.uid, 'meta', 'profile');
    getDoc(ref).then(d => {
      if (d.exists()) setProfile(d.data() as ProfileData);
    }).catch(() => {});
  }, [user]);

  const save = async () => {
    if (!user) { Toast.show({ type: 'error', text1: 'Требуется вход' }); return; }
    try {
      await setDoc(doc(db, 'users', user.uid, 'meta', 'profile'), { ...profile, updatedAt: serverTimestamp() });
      Toast.show({ type: 'success', text1: 'Сохранено' });
      setEditing(false);
    } catch (e: any) {
      Toast.show({ type: 'error', text1: 'Ошибка', text2: e.message || String(e) });
    }
  };

  const doSignOut = async () => {
    try {
      await signOut(auth);
    } catch (e) {
      Toast.show({ type: 'error', text1: 'Ошибка', text2: String(e) });
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <View style={styles.avatar}><Text style={styles.avatarText}>{initials(profile.name || user?.email || '')}</Text></View>
        <View style={{flex:1}}>
          <Text style={styles.title}>{profile.name || user?.displayName || 'Врач'}</Text>
          <Text style={styles.subtitle}>{profile.position || 'Должность не указана'}</Text>
          <Text style={styles.meta}>{profile.institution || ''}</Text>
        </View>
      </View>

      <View style={styles.card}>
        <Text style={styles.sectionTitle}>Контактные данные</Text>
        {editing ? (
          <>
            <TextInput style={styles.input} placeholder="Имя" value={profile.name || ''} onChangeText={v => setProfile(p => ({...p, name: v}))} />
            <TextInput style={styles.input} placeholder="Заведение" value={profile.institution || ''} onChangeText={v => setProfile(p => ({...p, institution: v}))} />
            <TextInput style={styles.input} placeholder="Должность" value={profile.position || ''} onChangeText={v => setProfile(p => ({...p, position: v}))} />
            <TextInput style={styles.input} placeholder="Контакт (тел/почта)" value={profile.contact || ''} onChangeText={v => setProfile(p => ({...p, contact: v}))} />
            <View style={{flexDirection:'row', gap:8}}>
              <Pressable style={[styles.button, styles.saveBtn]} onPress={save}><Text style={styles.buttonText}>Сохранить</Text></Pressable>
              <Pressable style={[styles.button, styles.cancelBtn]} onPress={() => setEditing(false)}><Text style={styles.buttonTextAlt}>Отмена</Text></Pressable>
            </View>
          </>
        ) : (
          <>
            <Text style={styles.fieldLabel}>Email</Text>
            <Text style={styles.fieldValue}>{user?.email ?? '—'}</Text>
            <Text style={styles.fieldLabel}>Контакт</Text>
            <Text style={styles.fieldValue}>{profile.contact ?? '—'}</Text>
            <View style={{flexDirection:'row', marginTop:12}}>
              <Pressable style={[styles.button, styles.editBtn]} onPress={() => setEditing(true)}><Text style={styles.buttonText}>Редактировать профиль</Text></Pressable>
              <Pressable style={[styles.button, styles.signOutBtn]} onPress={doSignOut}><Text style={styles.buttonText}>Выйти</Text></Pressable>
            </View>
          </>
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f8fafc' },
  header: { padding: 20, flexDirection: 'row', alignItems: 'center', backgroundColor: '#fff', borderBottomWidth: 1, borderColor: '#eef2ff' },
  avatar: { width: 72, height: 72, borderRadius: 36, backgroundColor: '#2563eb', alignItems: 'center', justifyContent: 'center', marginRight: 12 },
  avatarText: { color: '#fff', fontWeight: '700', fontSize: 20 },
  title: { fontSize: 18, fontWeight: '700' },
  subtitle: { color: '#475569', marginTop: 2 },
  meta: { color: '#94a3b8', marginTop: 6, fontSize: 12 },
  card: { margin: 16, padding: 16, borderRadius: 12, backgroundColor: '#fff', shadowColor: '#000', shadowOpacity: 0.03, shadowRadius: 8, elevation: 2 },
  sectionTitle: { fontWeight: '700', marginBottom: 8 },
  input: { borderWidth: 1, borderColor: '#eef2ff', padding: 10, borderRadius: 8, marginBottom: 10, backgroundColor: '#fbfdff' },
  button: { padding: 12, borderRadius: 8, alignItems: 'center', marginRight: 8 },
  editBtn: { backgroundColor: '#2563eb' },
  saveBtn: { backgroundColor: '#059669' },
  signOutBtn: { backgroundColor: '#ef4444' },
  cancelBtn: { backgroundColor: '#9ca3af' },
  fieldLabel: { color: '#64748b', marginTop: 8 },
  fieldValue: { fontSize: 16, fontWeight: '600', marginTop: 4 },
  buttonText: { color: '#fff', fontWeight: '700' },
  buttonTextAlt: { color: '#fff', fontWeight: '700' }
});
